var fs =  require("fs");
var path = require("path");
var ConfigurePath = function(){
	return path.join("assets","DefaultConfigure.json");
};
var getLogConfig = function(key){
	var configureContent = fs.readFileSync(ConfigurePath(),{encoding:"utf-8"});
  	var LogConfigure = JSON.parse(configureContent).log;
  	if(typeof key === "undefined")
  		return LogConfigure;
  	return LogConfigure[key];
}
exports.getLogConfig = getLogConfig;