var path = require('path');
var fs = require('fs');
/*
*Judge the application's configure is exists or not;
*paramters:
	appConfigPath type:string   
		the applocation configure's path
return
	if the configure is exist return the fullpath of it;
	else return false
*/
function isConfigureExists(appConfigPath){
	var filePath = path.join(process.cwd(),appConfigPath);
	var exists = fs.existsSync(filePath);
	if(exits){
		return filePath;
	}
	return false;
}
exports.setConfigure = function(appConfigPath){
	var filePath =  isConfigureExists(appConfigPath);
	if(!filePath){
		throw new Error("the application configure file '" +appConfigPath+"' is not exists");
	}
}