var server = require("./server");
var routerHandle = require("./routerHandle");
exports.createServer = function(){
	return server.createServer;
}

exports.setRouterHandle = function(handle){
	routerHandle.handle = handle;
}

exports.setStaticResourceDir = function(path){
	routerHandle.staticResourceDir = path ;
}

exports.globelConfigure = function(setting){
	require("./globelsetting").setConfigure(setting);
}
/*
	Config fastjosnweb
	Parameters:
		filePath  Type:string   [optional parameter]
		the configure file's  path. 
		if filename is undefined  ,defualt is configure.json
		if configure.json is not exists. use the fastjsonweb's DefaultConfigure.json.  
		more  see [url] DefaultConfigure.json
*/
exports.configure = function(filename){
	if(typeof filename === "undefined"){
		filename = "configure.json";
	}
	
}

