/*
  Get the MIME of respone and file
  read from MIME.json
*/
var fs = require("fs");
exports.getMime = function(name){
  var mime = fs.readFileSync("MIME.json",{encoding:"utf-8"});
  mime = JSON.parse(mime);
  if(!name || typeof name === "undefined")
    return mime;
  return mime[name];
}