/*
  Get the MIME of respone and file
  read from MIME.json
*/
var fs = require("fs");
var ConfigHelper = require("./ConfigHelper");
exports.getMime = function(name){
  var mimepath = ConfigHelper.getAssetsPath("MIME.json");
  var mime = fs.readFileSync(mimepath,{encoding:"utf-8"});
  mime = JSON.parse(mime);
  if(!name || typeof name === "undefined")
    return mime;
  return mime[name];
}