var SmartRouter = require("./SmartRouter");

var map = {
	"*":"{1}_*",
	"index":"__index",
	"*ndex":"{1}__*ndex",
	"i*dex":"{1}__i*dex",
	"*ex":"{1}_*ex",
	"*nd*":"{1}_{2}_*nd*"
}

var router = new SmartRouter({map:map});

console.log(router.get("hello")) //==> *
console.log(router.get("index")) // == index
console.log(router.get("Xndex")) // ===> *ndex
console.log(router.get("ijndex")) // ==> *ndex
console.log(router.get("Tex")) // ==>*ex
console.log(router.get("ande"))//===> *nd*

