exports.staticResourceDir = require("./globelsetting").setting.static;

exports.requestNotFound =  function(){
	this.execute = function(request,response){
		response.setStatus(404);
		var html = '<h1 align="center">404!Path  "'+request.url+'" is Error错误!</h1>';
		response.write(html,"html");
		response.end();
	};
}