var SESSIONTIME = require("./globelsetting").setting.sessiontimeout; 
var sessionData = require('./static_session');
//check  if the session is legal
exports.timeout = function(){
	if(!sessionData.data) return;
	var time = new Date().getTime();
	for(sid in sessionData.data){
		if(time - sessionData.data[sid].time > SESSIONTIME){
			delete(sessionData.data[sid]);
		}
	}
}